import React, {useContext, useEffect, useState} from "react";
import Modal from "react-bootstrap/Modal";
import {Button, Dropdown, Form, Row, Col} from "react-bootstrap";
import {createCompilation} from "../../http/CatalogApi";
import {Context} from "../../index";
import { fetchThemes } from "../../http/CatalogApi";
import { fetchTasks } from "../../http/CatalogApi";
import { fetchOneDevice } from "../../http/CatalogApi";

const CreateComp = ({show, onHide}) => {
    const [value, setValue] = useState('')
    const [currentTheme, setCurrentTheme] = useState(0)
    const [currentTask, setCurrentTask] = useState('')
    const {tasks} = useContext(Context)

    useEffect(() => {
        fetchThemes().then(data => {
            tasks.setThemes(data.rows)

        })
        fetchTasks().then(data => {
            tasks.SetTasks(data.rows)
            // console.log(tasks.tasks[1].TaskId)
        })
    }, [])

    const GetCurrentTasks = themeid => {
        let current_tasks = 0
        fetchOneDevice(themeid).then(data => {
            setCurrentTask(data)
            // console.log(tasks.tasks[1].TaskId)
        })
        console.log(currentTask)
        // let resulting = [];
        // for (let i = 0; i < data; i++) {
        //     resulting.push(<Dropdown.Item
        //         onClick={() => setResult(i)}
        //         key={i}
        //     >
        //         {i+1}
        //     </Dropdown.Item>);
        // }
        // return resulting;
        };
    GetCurrentTasks(1)

    const addType = () => {
        createCompilation({name: value}).then(data => {
            setValue('')
            onHide()
        })
    }


    return (
        <Modal
            show={show}
            onHide={onHide}
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Добавить тип
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <Dropdown className="mt-2 mb-2">
            <Dropdown.Toggle>{currentTheme.name || 'Выберите тему'}</Dropdown.Toggle>
            <Dropdown.Menu>
                {tasks.Themes.map(data =>
                    <Dropdown.Item
                        onClick={() => setCurrentTheme(data)}
                        key={data.id}
                    >
                        {data.name}
                    </Dropdown.Item>
                )}
            </Dropdown.Menu>    
            </Dropdown>
            <div>Текущие задания</div>
            <Dropdown className="mt-2 mb-2">
            <Dropdown.Toggle>{currentTheme.name || 'Выберите тему'}</Dropdown.Toggle>
            <Dropdown.Menu>
                {tasks.Themes.map(data =>
                    <Dropdown.Item
                        onClick={() => setCurrentTheme(data)}
                        key={data.id}
                    >
                        {data.name}
                    </Dropdown.Item>
                )}
            </Dropdown.Menu>    
            </Dropdown>
                <Form>
                    <Form.Control
                        value={value}
                        onChange={e => setValue(e.target.value)}
                        placeholder={"Введите название типа"}
                    />
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-danger" onClick={onHide}>Закрыть</Button>
                <Button variant="outline-success" onClick={addType}>Добавить</Button>
            </Modal.Footer>
        </Modal>
    );
};

export default CreateComp;
