import React, {useContext, useEffect, useState} from "react";
import { catalog } from "../http/CatalogApi";
import { useParams } from "react-router-dom";
import {observer} from "mobx-react-lite";
import '../styles/App.css'
import { Context } from "../index";
import { fetchOneTheme } from "../http/CatalogApi";
import Head from "../components/Head";
import { Button } from "react-bootstrap";
const Material = observer (() => {
    const {id} = useParams()
    const {tasks} = useContext(Context)
    const [material, setMaterial] = useState('загрузка...')
    useEffect(() => {
        fetchOneTheme(id).then(data => {
            setMaterial(data[0][0])
        })
    }, [])
    return(
        <div className="container">
            <Head></Head>
            <div className="text_start">{material.name}</div>
            <div className="text_material">{material.material}</div>
            <div>
            <div class="photo1">
                </div>
                <div class="photo2">
                    <img src="./img/photo2.png" alt="Alternate Text" style="width:164px" />
                </div>
                <div class="photo3">
                    <img src="./img/photo3.png" alt="Alternate Text" style="width:280px" />
                </div>
                <div class="photo4">
                    <img src="./img/photo4.png" alt="Alternate Text" style="width:230px" />
                </div>
                <div class="photo5">
                    <img src="./img/photo5.png" alt="Alternate Text" style="width:350px" />
                </div>
            </div>
            <Button style={{marginLeft:'47%'}}>завершить</Button>
        </div>
    );
})

export default Material;
