import React from 'react'
import { useState } from 'react'

export default function Tasks({task}) {
  const [reactive, setReactive] = useState('btn-light')
  const doActive = async() =>{
    console.log(reactive)
    setReactive('btn-primary')
    console.log(reactive)
}
const notActive = async() =>{ 
    console.log('')
    setReactive('btn-light')
}
const privet = async() =>{
  console.log('привет')
}
const eventlist = async() =>{
  eleme = document.getElementById(`${count + 1}_${task.TaskId}`)
  eleme.addEventListener('click', () =>{console.log('fgfg')})
  console.log('привет')
}
  let tasking = task.question.split(';')
  if (tasking[tasking.length - 1] == ''){
    tasking.pop()
  }
  let count = 0

  //document.getElementsByClassName(button')
  if (task.type == 1){
  return (
    <div>
      <div className='question_text'>
        ТИП 1
        Задание, {task.id} 
      </div>
      <div className='text_task'>
        {task.info}
      </div>
      <div>
        {tasking.map(data =>
        <div className={`text_tasks ${count = count + 1} ${task.TaskId}id`} onClick={privet}>
            
            {data}
            {task.ThemeId}
            {document.getElementsByClassName('text_task')}
        </div>
        )}
      </div>
      
    </div>
    // <div className="programms" style={{background: '#C7EEFF'}}>
    //   {themes.name}
    //   {themes.id}
    // </div>
  )}
  if (task.type == 2){
    return (
      <div>
      <div className='question_text'>
        ТИП 2
        Задание, {task.id} 
      </div>
      <div className='text_task'>
        {task.info}
      </div>
      <div>
      {tasking.map(data =>
        <div id={`${count}_${task.TaskId}`} className={`text_tasks ${count = count + 1} ${task.TaskId}id`}>
            {data}
            {task.ThemeId}
            {console.log(task.TaskId)}
            {eventlist(`${count}_${task.TaskId}`)}
            
        </div>
        )}
      </div>
      </div>
      // <div className="programms" style={{background: '#C7EEFF'}}>
      //   {themes.name}
      //   {themes.id}
      // </div>
    )
  }
}
