import React, {useContext, useEffect, useState} from "react";
import { catalog } from "../http/CatalogApi";
import { useParams } from "react-router-dom";
import {observer} from "mobx-react-lite";
import '../styles/App.css'
import { Context } from "../index";
import { fetchOneDevice } from "../http/CatalogApi";
import ItemsSection from "../components/ItemsSection";
import Head from "../components/Head";
const Compilation = observer (() => {
    const {id} = useParams()
    const {tasks} = useContext(Context)
    let temp
    document.cookie = "john=JOPa"
    useEffect(() => {
        fetchOneDevice(id).then(data => {
            tasks.SetTasks(data)
            console.log(data)
            console.log(tasks.tasks)
            console.log(tasks.Themes)
            // console.log(tasks.tasks[1].TaskId)
        })
    }, [])
    return(
        <div className="container">
            <Head></Head>
            <div className="text_start">Учебный материал</div>
            <div className="border"></div>
            <div>Привет</div>
            {tasks.tasks.map(data =>
                    <div><div>{data.TaskId}</div>
                    <div>{data.info}</div>
                    </div>
                )}

{tasks.Themes.map(data =>
                    <ItemsSection key={data.id} themes={data}></ItemsSection>
                )}
        </div>
    );
})

export default Compilation;
