import React from 'react'


export default function Tasks({task}) {
  let tasking = task.question.split(';')
  if (tasking[tasking.length - 1] == ''){
    tasking.pop()
  }
  if (task.type == 1){
  return (
    <div>
      <div className='question'>
        ТИП 1
        Задание, {task.id} 
      </div>
      <div className='text_task'>
        {task.info}
      </div>
      <div>
        {tasking.map(data =>
        <div>
            {data}
        </div>
        )}
      </div>
      
    </div>
    // <div className="programms" style={{background: '#C7EEFF'}}>
    //   {themes.name}
    //   {themes.id}
    // </div>
  )}
  if (task.type == 2){
    return (
      <div>
      <div className='question'>
        ТИП 2
        Задание, {task.id} 
      </div>
      <div className='text_task'>
        {task.info}
      </div>
      <div>
        {tasking.map(data =>
        <div>
            {data}
        </div>
        )}
      </div>
      </div>
      // <div className="programms" style={{background: '#C7EEFF'}}>
      //   {themes.name}
      //   {themes.id}
      // </div>
    )
  }
}
