import React, {useContext, useEffect, useState} from 'react';
import Modal from "react-bootstrap/Modal";
import {Button, Dropdown, Form, Row, Col} from "react-bootstrap";
import {Context} from "../../index";
import {createDevice, fetchBrands, fetchDevices, fetchTypes} from "../../http/CatalogApi";
import {observer} from "mobx-react-lite";

const CreateTask = observer(({show, onHide}) => {
    const {tasks} = useContext(Context)
    const [name, setName] = useState('')
    const [quality, setQuality] = useState(0)
    // const [price, setPrice] = useState(0)
    const [file, setFile] = useState(null)
    const [info, setInfo] = useState([])
    const [type, setType] = useState('')
    let massive = [1,2,3,4]
    let tasking = []
    let types = [1,2]
    const setTasking = (id, task) =>{
        tasking[id] = task
    }
    const GetQuestions = quest => {
        let content = [];
        for (let i = 0; i < quest; i++) {
            const item = quest[i];
            content.push(<Form.Control
                key={i}
                value={tasking[i]}
                onChange={e => setTasking(i, e.target.value + ';')}
                className="mt-3"
                placeholder={`Вопрос ${i + 1}`}
            />);
        }
        return content;
        };






    // useEffect(() => {
    //     fetchTypes().then(data => tasks.setTypes(data))
    //     fetchBrands().then(data => tasks.setBrands(data))
    // }, [])

    // const addInfo = () => {
    //     setInfo([...info, {title: '', description: '', number: Date.now()}])
    // }
    // const removeInfo = (number) => {
    //     setInfo(info.filter(i => i.number !== number))
    // }
    // const changeInfo = (key, value, number) => {
    //     setInfo(info.map(i => i.number === number ? {...i, [key]: value} : i))
    // }

    // const selectFile = e => {
    //     setFile(e.target.files[0])
    // }

    // const addDevice = () => {
    //     const formData = new FormData()
    //     formData.append('name', name)
    //     formData.append('price', `${price}`)
    //     formData.append('img', file)
    //     formData.append('brandId', tasks.selectedBrand.id)
    //     formData.append('typeId', tasks.selectedType.id)
    //     formData.append('info', JSON.stringify(info))
    //     createDevice(formData).then(data => onHide())
    // }

    return (
        <Modal
            show={show}
            onHide={onHide}
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Добавить задание
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Control
                            value={name}
                            onChange={e => setName(e.target.value)}
                            className="mt-3"
                            placeholder="Введите формулировку задания"
                        />
                    <Dropdown className="mt-2 mb-2">
                        <Dropdown.Toggle>{quality || 'Кол-вопросов'}</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {massive.map(data =>
                                <Dropdown.Item
                                    onClick={() => setQuality(data)}
                                    key={data}
                                >
                                    {data}
                                </Dropdown.Item>
                            )}
                        </Dropdown.Menu>    
                    </Dropdown>
                    {quality ? <div>{GetQuestions(quality)}</div> : <div></div>}
                    <Dropdown className="mt-2 mb-2">
                        <Dropdown.Toggle>"Выберите тип"</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {types.map(type =>
                                <Dropdown.Item
                                    onClick={() => tasks.setSelectedBrand(brand)}
                                    key={type}
                                >
                                    {type}
                                </Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown>

                    <Form.Control
                        // value={price}
                        // onChange={e => setPrice(Number(e.target.value))}
                        className="mt-3"
                        placeholder="Введите стоимость устройства"
                        type="number"
                    />
                    <Form.Control
                        className="mt-3"
                        type="file"
                        onChange={setName}
                    />
                    <hr/>
                    <Button
                        variant={"outline-dark"}
                        onClick={setName}
                    >
                        Добавить новое свойство
                    </Button>
                    {/* {info.map(i =>
                        <Row className="mt-4" key={i.number}>
                            <Col md={4}>
                                <Form.Control
                                    value={i.title}
                                    onChange={(e) => changeInfo('title', e.target.value, i.number)}
                                    placeholder="Введите название свойства"
                                />
                            </Col>
                            <Col md={4}>
                                <Form.Control
                                    value={i.description}
                                    onChange={(e) => changeInfo('description', e.target.value, i.number)}
                                    placeholder="Введите описание свойства"
                                />
                            </Col>
                            <Col md={4}>
                                <Button
                                    onClick={() => removeInfo(i.number)}
                                    variant={"outline-danger"}
                                >
                                    Удалить
                                </Button>
                            </Col>
                        </Row>
                    )} */}
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-danger" onClick={onHide}>Закрыть</Button>
                <Button variant="outline-success" onClick={setName}>Добавить</Button>
            </Modal.Footer>
        </Modal>
    );
});

export default CreateTask;
