import React from 'react'
import { useState } from 'react'

export default function Tasks({task}) {
  const [reactive, setReactive] = useState('btn-light')
  const doActive = async() =>{
    console.log(reactive)
    setReactive('btn-primary')
    console.log(reactive)
}
const notActive = async() =>{ 
    console.log('')
    setReactive('btn-light')
}
const privet = async(id) =>{
  console.log('привет', id)
}
  let tasking = task.question.split(';')
  if (tasking[tasking.length - 1] == ''){
    tasking.pop()
  }
  

  //document.getElementsByClassName(button')
  if (task.type == 1){
  return (
    <div>
      <div className='question_text'>
        ТИП 1
        Задание, {task.id} 
      </div>
      <div className='text_task'>
        {task.info}
      </div>
      <div>
        {tasking.map(data =>
        <div onClick={privet(data.id)}>
            {data}
        </div>
        )}
      </div>
      
    </div>
    // <div className="programms" style={{background: '#C7EEFF'}}>
    //   {themes.name}
    //   {themes.id}
    // </div>
  )}
  if (task.type == 2){
    return (
      <div>
      <div className='question_text'>
        ТИП 2
        Задание, {task.id} 
      </div>
      <div className='text_task'>
        {task.info}
      </div>
      <div>
        {tasking.map(data =>
        <div>
            {data}
        </div>
        )}
      </div>
      </div>
      // <div className="programms" style={{background: '#C7EEFF'}}>
      //   {themes.name}
      //   {themes.id}
      // </div>
    )
  }
}
