        this._genre = [0, 1]
        this._page = 1
        this._totalcount = 0
        this._limit = 4
        makeAutoObservable(this)
    }

    setThemes(theme) {
        this._themes = theme
    }

    SetGenres(genres) {
        this._genre = genres
    }

    get Themes() {
        return this._themes
    }
    
    get Genres() {
        return this._genre
    }

    get page() {
        return this._page
    }

    get limit() {
        return this._limit
    }

}
