import React, { useState } from "react";
import { registration, login } from "../http/UserApi";
import 'react-bootstrap'
import { Button, Form, NavLink } from "react-bootstrap";
import { LOGIN_ROUTE, REGISTRATION_ROUTE } from "../utils/consts";
import {useLocation, useHistory} from "react-router-dom";

const Auth =() => {
    const [Email, setEmail] = useState('')
    const [Login, setLogin] = useState('')
    const [Password, setPassword] = useState('')
    const location = useLocation()
    const history = useHistory()
    const type = location.pathname === REGISTRATION_ROUTE   
    const signIn = async () =>{
        console.log(setEmail)
        console.log(setLogin)
        console.log(setPassword)
        console.log('разделение')
        console.log(Email)
        console.log(Login)
        console.log(Password)
        if (type){
            const response123 = await registration(Login, Email, Password)
        }
        else{
            const logining = await login(Login, Password)
        }
        console.log('сработало')
    }
    return(
        <div>
            Auth
            <Form>
                {type ?
                    <div>
                        <Form.Control
                            placeholder = "Введите ваш email"
                            value = {Email}
                            onChange = {e=>setEmail(e.target.value)}
                        />
                                        <Form.Control
                            placeholder = "Введите ваш логин"
                            value = {Login}
                            onChange = {e=>setLogin(e.target.value)}
                        />
                        <Form.Control
                            placeholder = "Введите ваш пароль"
                            value = {Password}
                            onChange = {e=>setPassword(e.target.value)}
                        />
                        <NavLink to={LOGIN_ROUTE}>Залогинья!</NavLink>
                    </div>
                    :
                    <div>
                                        <Form.Control
                            placeholder = "Введите ваш логин"
                            value = {Login}
                            onChange = {e=>setLogin(e.target.value)}
                        />
                        <Form.Control
                            placeholder = "Введите ваш пароль"
                            value = {Password}
                            onChange = {e=>setPassword(e.target.value)}
                        />
                        <NavLink to={REGISTRATION_ROUTE}>Зарегистрируйся!</NavLink>
                    </div>
                }
            </Form>
            <Button onClick={signIn}>Нажми</Button>
        </div>
    );
};

export default Auth;
