import {makeAutoObservable} from 'mobx'
export default class CatalogStore {
    constructor(){
        this._books = [
            {id: 1, name: "Чидори", author: "Казаке", href: "none"},
            {id: 2, name: "Чидор1", author: "Казаке1", href: "none"},
            {id: 3, name: "Чидор1", author: "Казаке1", href: "none"}
        ]
        this._genre = [0, 1]
        this._page = 1
        this._totalcount = 0
        this._limit = 3
        makeAutoObservable(this)
    }

    SetBooks(books) {
        this._books = books
    }

    SetGenres(genres) {
        this._genre = genres
    }

    get Books() {
        return this._books
    }
    
    get Genres() {
        return this._genre
    }

    get page() {
        return this._page
    }

    get limit() {
        return this._limit
    }

}
