import React, {useState} from 'react';
import {Button, Container} from "react-bootstrap";
import CreateTask from "../components/modals/CreateTask";
import CreateTheme from "../components/modals/CreateTheme";


const Admin = () => {
    const [taskVisible, setTaskVisible] = useState(false)
    const [themeVisible, setThemeVisible] = useState(false)
    const [compilationVisible, setCompilationVisible] = useState(false)

    return (
        <Container className="d-flex flex-column">
            <Button
                variant={"outline-dark"}
                className="mt-4 p-2"
                onClick={() => setTaskVisible(true)}
            >
                Добавить задание
            </Button>
            <Button
                variant={"outline-dark"}
                className="mt-4 p-2"
                onClick={() => setTHemeVisible(true)}
            >
                Добавить тему
            </Button>
            <Button
                variant={"outline-dark"}
                className="mt-4 p-2"
                onClick={() => setCompilationVisible(true)}
            >
                Добавить подборку
            </Button>
            <CreateTask show={taskVisible} onHide={() => setTaskVisible(false)}/>
            <CreateTheme show={themeVisible} onHide={() => setThemeVisible(false)}/>
            {/* <CreateType show={typeVisible} onHide={() => setTypeVisible(false)}/> */}
        </Container>
    );
};

export default Admin;
